mapper.sql:
where 1 = 1 and state = 1

	and id != #{id}


	and name = #{name}


	and exp_name = #{exp_name}


	and code = #{code}

mapper.update:
update t_goods set name = #{name},exp_name = #{exp_name},
content = #{content},code = #{code},brandId = #{brandId},store = #{store},
typeId = #{typeId},ids = #{ids} where id = #{id}
